    public static function table(Table $table): Table
    {
        return $table
            ->columns([
                Tables\Columns\TextColumn::make('name')
                    ->label('Ad')
                    ->searchable()
                    ->sortable(),
                    
                Tables\Columns\TextColumn::make('category')
                    ->label('Kategori')
                    ->searchable()
                    ->sortable()
                    ->badge()
                    ->formatStateUsing(fn ($state) => match($state) {
                        'general' => 'Genel',
                        'room' => 'Oda',
                        'bathroom' => 'Banyo',
                        'kitchen' => 'Mutfak',
                        'entertainment' => 'Eğlence',
                        'services' => 'Hizmetler',
                        'accessibility' => 'Erişilebilirlik',
                        'outdoor' => 'Dış Mekan',
                        'transportation' => 'Ulaşım',
                        'business' => 'İş',
                        'kids' => 'Çocuk',
                        'sports' => 'Spor',
                        'food' => 'Yiyecek & İçecek',
                        default => $state,
                    })
                    ->colors([
                        'primary' => 'general',
                        'success' => 'room',
                        'warning' => 'bathroom',
                        'info' => 'kitchen',
                        'danger' => 'entertainment',
                        'secondary' => 'services',
                        'tertiary' => fn ($state) => in_array($state, [
                            'accessibility', 'outdoor', 'transportation', 
                            'business', 'kids', 'sports', 'food'
                        ]),
                    ]),
                    
                Tables\Columns\IconColumn::make('icon')
                    ->label('İkon')
                    ->icon(fn ($state) => 
                        // Eğer state heroicon-o- ile başlamıyorsa, ekleyelim
                        $state ? 
                            (str_starts_with($state, 'heroicon-o-') ? $state : 'heroicon-o-sparkles') 
                            : 'heroicon-o-sparkles'
                    ),
                    
                Tables\Columns\IconColumn::make('is_active')
                    ->label('Aktif')
                    ->boolean()
                    ->sortable(),
                    
                Tables\Columns\TextColumn::make('sort_order')
                    ->label('Sıralama')
                    ->numeric()
                    ->sortable(),
                    
                Tables\Columns\TextColumn::make('created_at')
                    ->label('Oluşturulma')
                    ->dateTime('d.m.Y H:i')
                    ->sortable()
                    ->toggleable(isToggledHiddenByDefault: true),
                    
                Tables\Columns\TextColumn::make('updated_at')
                    ->label('Güncelleme')
                    ->dateTime('d.m.Y H:i')
                    ->sortable()
                    ->toggleable(isToggledHiddenByDefault: true),
            ])
            ->filters([
                //
            ])
            ->actions([
                Tables\Actions\EditAction::make(),
            ])
            ->bulkActions([
                Tables\Actions\BulkActionGroup::make([
                    Tables\Actions\DeleteBulkAction::make(),
                ]),
            ]);
    }

    public static function getRelations(): array
    {
        return [
            RelationManagers\HotelsRelationManager::class,
        ];
    }

    public static function getPages(): array
    {
        return [
            'index' => Pages\ListHotelAmenities::route('/'),
            'create' => Pages\CreateHotelAmenity::route('/create'),
            'edit' => Pages\EditHotelAmenity::route('/{record}/edit'),
        ];
    }
}